  return deps.reduce(
    (acc, pkg) => {
      if (isRNPMPlugin(pkg)) {
        return acc.concat(pkg);
      }
      if (isReactNativePlugin(pkg)) {
        const pkgJson = readPackage(path.join(folder, 'node_modules', pkg));
