  let assetName;
  if (match) {
    assetName = filename.replace(re, ext);
  } else if (platformExt != null) {
    assetName = filename.replace(new RegExp(`\\.${ platformExt }\\${ ext }`), ext);
  } else {
    assetName = filename;
  }

  return {
    resolution: resolution,
    assetName: assetName,
    type: ext.slice(1),
    name: path.basename(assetName, ext),
