module.exports = class AsyncTaskGroup {
  constructor() {
    this._runningTasks = new Set();
    this._resolve = null;
    this.done = new Promise(resolve => this._resolve = resolve);
  }

  start(taskHandle) {
    this._runningTasks.add(taskHandle);
