      this._eagerModules.push(id);
    }
  }

  addAsset(asset) {
    this._assets.push(asset);
  }

  // Synchronously load a file path.
  _loadFilename(path) {
    const module = this._modules[path];
    if (!module) {
      throw new Error('Could not find file "' + path + '" in preloaded files.');
    }
