function init(projectDir, argsOrName) {
  console.log('Setting up new React Native app in ' + projectDir);
  const env = yeoman.createEnv(
  undefined,
  undefined,
  new CreateSuppressingTerminalAdapter());


  env.register(
  require.resolve(path.join(__dirname, '../generator')),
